import { Component } from 'react'
import Section from "../components/Section"
import Color from "../components/Color"
import Headings from "../components/Headings"
import Paragraph from '../components/Paragraph'
import Menu from '../components/Menu'

class Container extends Component {

    constructor(props) {
        super(props)

        this.state = {
            font: 'sans-serif',
            color: [
                'rgba( 255, 0, 0, 0, .3)',
                'rgba( 255, 0, 0, 0, .8 )',
                'red'
            ]
        }

        this.changeFont = this.changeFont.bind(this)
        this.changeColor = this.changeColor.bind(this)
    }

    changeFont(e) {
        this.setState({
            font: (this.state.font == 'serif' ? 'sans-serif' : 'serif')
        })
    }

    changeColor(e) {
        console.log(e.target.value)

        switch(e.taget.value) {
            case 'red':
                this.setState({
                    color: [
                        'red',
                        'rgba( 255, 0, 0, .3 )',
                        'rgba( 255, 0, 0, .8 )',
                        'red'
                    ]
                })
            break
            case 'green':
                this.setState({
                    color: [
                        'rgba( 0, 255, 0, .3',
                        'rgba( 0, 255, 0, .8 )',
                        'green'
                    ]
                })
            default:
                this.setState({
                    color: [
                        'rgba( 255, 0, 0, .3',
                        'rgba( 255, 0, 0, .8 )',
                        'red'
                    ]
                })
        }
    }

    render() {
        return (
            <>
            <Menu changeFont={this.chagneFont} changeColor={ this.changeColor }/>
            <Section title="01.Typography">
                <Headings font={ this.state.font } />
                <Paragraph font={ this.state.font }/>
            </Section>
            <Section title="02.Color">
                <Color colors={["white", "rgba(0, 0, 0, .19)", "rgba(0, 0, 0, .39)", "rgba(0, 0, 0, .59)", "rgba(0, 0, 0, .79", "rgba(0, 0, 0, .89", "black"]}/>
                <Color colors={ this.state.color } />
            </Section>
            </>
        )
    }
}

export default Container
